// export const useCarrinho = () => useContext(CarrinhoContext);

import React, { createContext, useState, useContext } from "react";

const CarrinhoContext = createContext();

export const CarrinhoProvider = ({ children }) => {
  const [carrinho, setCarrinho] = useState([]);

  // Adicionar item ao carrinho (agora considerando a quantidade)
  const addToCart = (produto) => {
    setCarrinho((prev) => {
      const existingItem = prev.find((item) => item.id === produto.id);
      if (existingItem) {
        return prev.map((item) =>
          item.id === produto.id ? { ...item, quantidade: (item.quantidade || 1) + 1 } : item
        );
      }
      return [...prev, { ...produto, quantidade: 1 }];
    });
  };

  // Remover UM item do carrinho
  const removeFromCart = (id) => {
    setCarrinho((prev) => {
      return prev
        .map((item) => {
          if (item.id === id) {
            if (item.quantidade > 1) {
              return { ...item, quantidade: item.quantidade - 1 };
            } else {
              return null; // Remove o item se a quantidade chegar a zero
            }
          }
          return item;
        })
        .filter(Boolean); // Remove os itens nulos da lista
    });
  };

  // Limpar carrinho
  const clearCart = () => setCarrinho([]);

  return (
    <CarrinhoContext.Provider value={{ carrinho, addToCart, removeFromCart, clearCart }}>
      {children}
    </CarrinhoContext.Provider>
  );
};

export const useCarrinho = () => useContext(CarrinhoContext);
