import React from "react";
import "./carrinhoLateral.css";
import { useNavigate } from "react-router-dom";


const CarrinhoLateral = ({ isOpen, onClose, itens , removeFromCart , clearCart , setIsModalOpen }) => {
  const navigate = useNavigate();

  const handlePagamento = () => {
    const isAuthenticated = localStorage.getItem("isAuthenticated") === "true";

    if (!isAuthenticated) {
      setIsModalOpen(true); // Abre o modal do Header para login
    } else {
      navigate("/pagamento"); // Redireciona para pagamento se j√° estiver logado
    }
  };


  const calcularTotal = () => {
    return itens.reduce((acc, item) => {
      const quantidade = item.quantidade || 1; 
      return acc + item.pre√ßo * quantidade;
    }, 0);
  };

  return (
    <div className={`carrinho-lateral ${isOpen ? "open" : ""}`}>
      <div className="carrinho-header">
        <h2>Carrinho</h2>
        <button className="close-button" onClick={onClose}>
          Fechar
        </button>
      </div>
      <div className="carrinho-conteudo">
        {itens.length === 0 ? (
          <p>Seu carrinho est√° vazio.</p>
        ) : (
          <ul>
            {itens.map((item) => (
              <li key={item.id} className="carrinho-item">
                <span>{item.nome}</span>
                <span>{item.quantidade} Un</span>
                <span>Valor R$ {(item.pre√ßo * item.quantidade).toFixed(2)}</span>
                <button className="trash-icon" 
                onClick={() => {
                  removeFromCart(item.id);
                }}>üóëÔ∏è</button>
              </li>
            ))}
          </ul>
          
        )}
         <div className="carrinho-total">
            <strong>Total:</strong> R$ {calcularTotal().toFixed(2)}
          </div>
          <div className="botoes">
          {itens.length > 0 && (
            <button className="add-to-cart-button" onClick={clearCart}>Limpar carrinho</button> )}
            <button className="add-to-cart-button pagamento" onClick={navigate("/pagamento")}>Ir para pagamento</button>
          </div>
      </div>
    </div>
  );
};

export default CarrinhoLateral;
