import React from "react";
import "./carrinhoLateral.css";
import { useCarrinho } from "./carrinhoContext";

const CarrinhoLateral = ({ isOpen, onClose }) => {
  const { carrinho, removeFromCart, clearCart } = useCarrinho();

  // Calcular o total do carrinho
  const calcularTotal = () => {
    return carrinho.reduce((acc, item) => {
      return acc + item.preço * item.quantidade;
    }, 0);
  };

  return (
    <div className={`carrinho-lateral ${isOpen ? "open" : ""}`}>
      <div className="carrinho-header">
        <h2>Carrinho</h2>
        <button className="close-button" onClick={onClose}>Fechar</button>
      </div>

      <div className="carrinho-conteudo">
        {carrinho.length === 0 ? (
          <p>Seu carrinho está vazio.</p>
        ) : (
          <>
            <ul>
              {carrinho.map((item) => (
                <li key={item.id} className="carrinho-item">
                  <span>{item.nome}</span>
                  <span>{item.quantidade} Un</span>
                  <span>Valor R$ {(item.preço * item.quantidade).toFixed(2)}</span>
                  <button
                    className="cart-item-remove"
                    onClick={() => removeFromCart(item.id)}
                  >
                    Remover 1
                  </button>
                </li>
              ))}
            </ul>
            <div className="carrinho-total">
              <strong>Total:</strong> R$ {calcularTotal().toFixed(2)}
            </div>
          </>
        )}
      </div>

      {carrinho.length > 0 && (
        <button className="clear-cart-button" onClick={clearCart}>
          Limpar Carrinho
        </button>
      )}
    </div>
  );
};

export default CarrinhoLateral;
