import React from "react";
import "./carrinhoLateral.css";
import { useCarrinho } from "./carrinhoContext";

const CarrinhoLateral = ({ isOpen, onClose }) => {
  const { carrinho, removeFromCart } = useCarrinho();

  if (!isOpen) return null;

  return (
    <div className={`carrinho-lateral ${isOpen ? "open" : ""}`}>
      <div className="carrinho-header">
        <h2>Carrinho</h2>
        <button className="close-button" onClick={onClose}>
          Fechar
        </button>
      </div>
      <div className="carrinho-conteudo">
      {carrinho.map((item) => (
        <div key={item.id} className="flex items-center justify-between my-2">
          <img src={item.src} alt={item.nome} className="w-16 h-16 object-cover" />
          <div>
            <p>{item.nome}</p>
            <p>{item.valor}</p>
          </div>
          <button className="text-red-500" onClick={() => removeFromCart(item.id)}>
            Remover
          </button>
        </div>
      ))}
      <button className="mt-5 bg-green-500 text-white px-4 py-2 rounded">Checkout</button>
    
      </div>
    </div>
  );
};

export default CarrinhoLateral;
