import React, { useState } from "react";

const ModalProduto = ({ produto, isOpen, onClose, addToCart }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
      <div className="bg-white p-5 rounded-lg shadow-lg w-96">
        <button className="text-red-500 float-right" onClick={onClose}>
          X
        </button>
        <img src={produto.src} alt={produto.nome} className="w-full h-48 object-cover" />
        <h2 className="text-lg font-bold mt-2">{produto.nome}</h2>
        <p className="text-gray-700">Preço: {produto.valor}</p>
        <button
          className="mt-3 bg-blue-500 text-white px-4 py-2 rounded"
          onClick={() => {
            addToCart(produto);
            onClose();
          }}
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default ModalProduto;
