import React, { useEffect, useState } from "react";

const Pagamento = () => {
  const [carrinhoItens, setCarrinhoItens] = useState([]);

  useEffect(() => {
    const carrinhoSalvo = JSON.parse(localStorage.getItem("carrinho")) || [];
    setCarrinhoItens(carrinhoSalvo);
  }, []);

  const calcularTotal = () => {
    return carrinhoItens.length > 0
      ? carrinhoItens.reduce((total, item) => total + (item.preco || 0) * item.quantidade, 0).toFixed(2)
      : "0.00";
  };

  return (
    <div>
      <h2>Checkout</h2>
      {carrinhoItens.length === 0 ? (
        <p>Seu carrinho está vazio.</p>
      ) : (
        <ul>
          {carrinhoItens.map((item) => (
            <li key={item.id}>
              {item.nome} - {item.quantidade} x R$ {item.preco?.toFixed(2) || "0.00"}
            </li>
          ))}
        </ul>
      )}
      <p>Total: R$ {calcularTotal()}</p>
    </div>
  );
};

export default Pagamento;
